mapper.select:

select user_identifier_id as userIdentifierId, email, password, name, address, phone_number as phoneNumber, profile_image_url as profileImageUrl, profile_image_alt as profileImageAlt, refresh_token as refreshToken, token_expiry as tokenExpiry, is_active as isActive, role
from user 
where email = #{email}


select user_identifier_id as userIdentifierId, email, password, name, address, phone_number as phoneNumber, profile_image_url as profileImageUrl, profile_image_alt as profileImageAlt, refresh_token as refreshToken, token_expiry as tokenExpiry, is_active as isActive, role
from user 
where email = #{email}

mapper.update:
update user
set refresh_token = #{refreshToken}, token_expiry = #{tokenExpiry}, is_active = #{isActive}
where user_identifier_id = #{id}
