mapper.select:

select id, email, password, name, address, role
from user 
where email = #{email}


select id, name, address, role, platform
from user
where id = #{id}

mapper.insert:
insert into user (id, email, password, name, address

    , platform

)
values (
  #{id},
  #{email},
  #{password},
  #{name},
  #{address}

    , #{platform}

)
