mapper.update:
update product


    name = #{name},



    price = #{price},



    description = #{description},



    discount_rate = #{discountRate},



    discount_price = #{discountPrice},



    thumbnail_image_name = #{thumbnailImageName}



    thumbnail_image_url = #{thumbnailImageUrl}


where id = #{id}

update product_option


    description = #{description},



    add_price = #{addPrice}


where id = #{id}
