mapper.select:
select id, user_id as userId, name, description, thumbnail_image_url as thumbnailImageUrl, thumbnail_image_alt as thumbnailImageAlt, created_at as createdAt, updated_at as updatedAt
from store


    where user_id = #{userId}



    where id = #{storeId}

select case when count(*) > 0 then true else false end
from store
where name = #{name}

select case when count(*) > 0 then true else false end
from store
where user_id = #{userId}
